export const mapRawToProprio = (data: any): Proprio => {
  return {
    cod: data.cod,
    nome_equipamento: data.nome,
    tipo_logradouro: '',
    nome_logradouro: '',
    numero_imovel: '',
    bairro: '',
    regional: data.reg,
    lat: data.lat,
    lng: data.lng,
    prioridade: determinePriority(data.nome)
  };
};

export const MOCK_PROPRIOS: Proprio[] = RAW_DATA.map(mapRawToProprio);

export const REGIONALS = Object.keys(REGIONAL_CENTERS).sort();

export const getSimulationRoute = (regional?: string): RoutePoint[] => {
  const center = regional ? REGIONAL_CENTERS[regional] : REGIONAL_CENTERS["CENTRO-SUL"];
  if (!center) return [];
  // Generate a circular route around the center
  const points: RoutePoint[] = [];
  const r = 0.005; // approx 500m
  const now = Date.now();
  for(let i=0; i<360; i+=30) {
    const rad = i * Math.PI / 180;
    points.push({
      lat: center.lat + r * Math.cos(rad),
      lng: center.lng + r * Math.sin(rad),
      timestamp: now + i * 1000
    });
  }
  return points;
};
